import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Courses.css';

// Tipo para os dados do curso
interface Course {
  id: number;
  title: string;
  instructor: string;
  level: string;
  duration: string;
  description: string;
  image: string;
  price: number;
  rating: number;
}

const Courses: React.FC = () => {
  // Dados de exemplo de cursos
  const [courses] = useState<Course[]>([
    {
      id: 1,
      title: "Desenvolvimento Web Completo",
      instructor: "João Silva",
      level: "Iniciante a Avançado",
      duration: "40 horas",
      description: "Aprenda HTML, CSS, JavaScript, React, Node.js e mais neste curso completo.",
      image: "/images/curso-web.jpg",
      price: 97.00,
      rating: 4.8
    },
    {
      id: 2,
      title: "JavaScript Moderno",
      instructor: "Maria Oliveira",
      level: "Intermediário",
      duration: "25 horas",
      description: "Domine os recursos modernos do JavaScript, incluindo ES6+, Promises e Async/Await.",
      image: "/images/curso-js.jpg",
      price: 79.90,
      rating: 4.7
    },
    {
      id: 3,
      title: "React para Iniciantes",
      instructor: "Pedro Santos",
      level: "Iniciante",
      duration: "20 horas",
      description: "Aprenda os fundamentos do React e crie suas primeiras aplicações.",
      image: "/images/curso-react.jpg",
      price: 69.90,
      rating: 4.9
    },
  ]);

  const [filter, setFilter] = useState('');

  // Filtra os cursos com base na entrada do usuário
  const filteredCourses = courses.filter(course => 
    course.title.toLowerCase().includes(filter.toLowerCase()) ||
    course.instructor.toLowerCase().includes(filter.toLowerCase()) ||
    course.level.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="courses-container container">
      <h1>Nossos Cursos</h1>
      
      <div className="filter-section">
        <input 
          type="text" 
          placeholder="Buscar cursos..." 
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="search-input"
        />
      </div>
      
      <div className="courses-grid">
        {filteredCourses.map(course => (
          <div className="course-card" key={course.id}>
            <div className="course-image">
              <img src={course.image} alt={course.title} />
            </div>
            <div className="course-info">
              <h2>{course.title}</h2>
              <p className="instructor">Instrutor: {course.instructor}</p>
              <div className="course-meta">
                <span className="level">{course.level}</span>
                <span className="duration">{course.duration}</span>
              </div>
              <p className="description">{course.description}</p>
              <div className="course-footer">
                <span className="price">R$ {course.price.toFixed(2)}</span>
                <span className="rating">★ {course.rating.toFixed(1)}</span>
              </div>
              <Link to={`/course/${course.id}`} className="btn-details">Ver Detalhes</Link>
            </div>
          </div>
        ))}
      </div>
      
      {filteredCourses.length === 0 && (
        <div className="no-courses">
          <p>Nenhum curso encontrado com o termo "{filter}".</p>
        </div>
      )}
    </div>
  );
};

export default Courses;
