import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../styles/CourseDetail.css';

// Tipo para os dados do curso
interface Course {
  id: number;
  title: string;
  instructor: string;
  level: string;
  duration: string;
  description: string;
  image: string;
  price: number;
  rating: number;
  modules: Module[];
  requirements: string[];
  goals: string[];
}

interface Module {
  id: number;
  title: string;
  duration: string;
  lessons: Lesson[];
}

interface Lesson {
  id: number;
  title: string;
  duration: string;
  isFree: boolean;
}

const CourseDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [course, setCourse] = useState<Course | null>(null);
  const [loading, setLoading] = useState(true);
  const [activeModule, setActiveModule] = useState<number | null>(null);

  useEffect(() => {
    // Simulando busca de dados de um curso específico
    // Em um ambiente real, isso seria uma chamada de API
    setTimeout(() => {
      // Dados de exemplo
      const mockCourse: Course = {
        id: parseInt(id || '1'),
        title: "Desenvolvimento Web Completo",
        instructor: "João Silva",
        level: "Iniciante a Avançado",
        duration: "40 horas",
        description: "Aprenda HTML, CSS, JavaScript, React, Node.js e mais neste curso completo. Este curso abrange tudo o que você precisa saber para se tornar um desenvolvedor web full-stack, desde os conceitos básicos até técnicas avançadas utilizadas no mercado de trabalho atual.",
        image: "/images/curso-web.jpg",
        price: 97.00,
        rating: 4.8,
        modules: [
          {
            id: 1,
            title: "Introdução ao Desenvolvimento Web",
            duration: "5 horas",
            lessons: [
              { id: 1, title: "O que é desenvolvimento web?", duration: "15 min", isFree: true },
              { id: 2, title: "Ferramentas necessárias", duration: "20 min", isFree: true },
              { id: 3, title: "Configurando seu ambiente", duration: "30 min", isFree: false },
            ]
          },
          {
            id: 2,
            title: "HTML5 Fundamental",
            duration: "8 horas",
            lessons: [
              { id: 4, title: "Estrutura básica do HTML", duration: "25 min", isFree: false },
              { id: 5, title: "Tags principais e semântica", duration: "40 min", isFree: false },
              { id: 6, title: "Formulários e validação", duration: "35 min", isFree: false },
            ]
          },
          {
            id: 3,
            title: "CSS3 e Estilização",
            duration: "10 horas",
            lessons: [
              { id: 7, title: "Seletores e propriedades", duration: "30 min", isFree: false },
              { id: 8, title: "Flexbox e Grid Layout", duration: "45 min", isFree: false },
              { id: 9, title: "Responsividade e Media Queries", duration: "50 min", isFree: false },
            ]
          },
        ],
        requirements: [
          "Conhecimento básico de informática",
          "Um computador com acesso à internet",
          "Disposição para aprender",
        ],
        goals: [
          "Criar sites responsivos do zero",
          "Desenvolver aplicações web com React",
          "Implementar APIs com Node.js",
          "Entender os princípios de UX/UI",
        ]
      };
      
      setCourse(mockCourse);
      setLoading(false);
      
      // Define o primeiro módulo como ativo inicialmente
      if (mockCourse.modules.length > 0) {
        setActiveModule(mockCourse.modules[0].id);
      }
    }, 800); // Simula um pequeno atraso de carregamento
  }, [id]);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Carregando detalhes do curso...</p>
      </div>
    );
  }

  if (!course) {
    return (
      <div className="error-container">
        <h2>Curso não encontrado</h2>
        <p>O curso que você está procurando não existe ou foi removido.</p>
        <Link to="/courses" className="btn-back">Voltar para Cursos</Link>
      </div>
    );
  }

  return (
    <div className="course-detail-container container">
      <div className="course-header">
        <div className="course-header-content">
          <h1>{course.title}</h1>
          <p className="course-description">{course.description}</p>
          <div className="course-meta-detail">
            <span className="instructor">Instrutor: {course.instructor}</span>
            <span className="level">Nível: {course.level}</span>
            <span className="duration">Duração: {course.duration}</span>
            <span className="rating">Avaliação: ★ {course.rating.toFixed(1)}</span>
          </div>
        </div>
        <div className="course-image-detail">
          <img src={course.image} alt={course.title} />
        </div>
      </div>

      <div className="course-action">
        <div className="price-box">
          <span className="price-amount">R$ {course.price.toFixed(2)}</span>
          <button className="btn-enroll">Matricular-se Agora</button>
          <button className="btn-cart">Adicionar ao Carrinho</button>
        </div>
      </div>

      <div className="course-content">
        <div className="content-section">
          <h2>Conteúdo do Curso</h2>
          <div className="modules-accordion">
            {course.modules.map(module => (
              <div className="module-item" key={module.id}>
                <div 
                  className={`module-header ${activeModule === module.id ? 'active' : ''}`}
                  onClick={() => setActiveModule(activeModule === module.id ? null : module.id)}
                >
                  <h3>{module.title}</h3>
                  <div className="module-meta">
                    <span>{module.duration}</span>
                    <span>{module.lessons.length} aulas</span>
                    <span className="toggle-icon">{activeModule === module.id ? '−' : '+'}</span>
                  </div>
                </div>
                
                {activeModule === module.id && (
                  <div className="module-content">
                    <ul className="lessons-list">
                      {module.lessons.map(lesson => (
                        <li key={lesson.id} className="lesson-item">
                          <div className="lesson-info">
                            <span className="lesson-title">{lesson.title}</span>
                            <span className="lesson-duration">{lesson.duration}</span>
                          </div>
                          {lesson.isFree && <span className="free-badge">Grátis</span>}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        <div className="about-course">
          <h2>Sobre Este Curso</h2>
          
          <div className="requirements">
            <h3>Pré-requisitos</h3>
            <ul>
              {course.requirements.map((req, index) => (
                <li key={index}>{req}</li>
              ))}
            </ul>
          </div>
          
          <div className="goals">
            <h3>O Que Você Vai Aprender</h3>
            <ul>
              {course.goals.map((goal, index) => (
                <li key={index}>{goal}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
