import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/NavBar.css';

const NavBar: React.FC = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="navbar">
      <div className="container navbar-container">
        <Link to="/" className="logo">
          CursoEduca
        </Link>

        <button 
          className="mobile-menu-btn" 
          onClick={toggleMenu}
          aria-label="Menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>

        <nav className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          <ul>
            <li>
              <Link to="/" onClick={() => setIsMenuOpen(false)}>Home</Link>
            </li>
            <li>
              <Link to="/courses" onClick={() => setIsMenuOpen(false)}>Cursos</Link>
            </li>
            <li>
              <Link to="/about" onClick={() => setIsMenuOpen(false)}>Sobre</Link>
            </li>
            <li>
              <Link to="/dashboard" onClick={() => setIsMenuOpen(false)}>Minha Conta</Link>
            </li>
            <li>
              <a href="#contato" onClick={() => setIsMenuOpen(false)}>Contato</a>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default NavBar;
