    }
  ]);

  const handleLevelToggle = (level: string) => {
    setSelectedLevels(prev =>
      prev.includes(level)
        ? prev.filter(l => l !== level)
        : [...prev, level]
    );
  };

  const handleTechToggle = (tech: string) => {
    setSelectedTechs(prev =>
      prev.includes(tech)
        ? prev.filter(t => t !== tech)
        : [...prev, tech]
    );
  };

  const clearFilters = () => {
    setSearchTerm('');
    setSelectedLevels([]);
    setSelectedTechs([]);
  };

  const filteredCourses = courses.filter(course => {
    const matchesSearch = course.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         course.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesLevel = selectedLevels.length === 0 || selectedLevels.includes(course.level);
    const matchesTech = selectedTechs.length === 0 || 
                       course.technologies.some(tech => selectedTechs.includes(tech));

    return matchesSearch && matchesLevel && matchesTech;
  });

  const getLevelIcon = (level: string) => {
    switch (level) {
      case 'iniciante': return 'üå±';
      case 'intermediario': return 'üöÄ';
      case 'avancado': return '‚≠ê';
      default: return 'üìö';
    }
  };

  const getTechIcon = (tech: string) => {
    switch (tech) {
      case 'html-css': return 'üåê';
      case 'javascript': return 'üíõ';
      case 'react': return '‚öõÔ∏è';
      case 'node': return 'üü¢';
      case 'python': return 'üêç';
      case 'java': return '‚òï';
      default: return 'üíª';
    }
  };

  return (
    <div className="courses-container">
      <section className="filters-section">
        <div className="search-bar">
          <input
            type="text"
            className="search-input"
            placeholder="Buscar cursos..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <FaSearch className="search-icon" />
        </div>

        <div className="filters-container">
          <div className="filter-group">
            <h3>
              <FaGraduationCap />
              N√≠vel
            </h3>
            <div className="filter-options">
              {levels.map(level => (
                <button
                  key={level}
                  className={`filter-btn ${selectedLevels.includes(level) ? 'active' : ''}`}
                  onClick={() => handleLevelToggle(level)}
                >
                  {getLevelIcon(level)} {level.charAt(0).toUpperCase() + level.slice(1)}
                </button>
              ))}
            </div>
          </div>

          <div className="filter-group">
            <h3>
              <FaCode />
              Tecnologias
            </h3>
            <div className="tech-options">
              {technologies.map(tech => (
                <button
                  key={tech}
                  className={`filter-btn ${selectedTechs.includes(tech) ? 'active' : ''}`}
                  onClick={() => handleTechToggle(tech)}
                >
                  {getTechIcon(tech)} {tech.toUpperCase()}
                </button>
              ))}
            </div>
          </div>

          {(selectedLevels.length > 0 || selectedTechs.length > 0 || searchTerm) && (
            <button className="clear-filters" onClick={clearFilters}>
              <FaTimes /> Limpar filtros
            </button>
          )}
        </div>
      </section>

      <div className="courses-grid">
        {filteredCourses.length > 0 ? (
          filteredCourses.map(course => (
            <div key={course.id} className="course-card">
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <div className="course-tech-stack">
                {course.technologies.map(tech => (
                  <span key={tech} className="tech-tag" title={tech.toUpperCase()}>
                    {getTechIcon(tech)}
                  </span>
                ))}
              </div>
              <div className="course-info">
                <span className="level">
                  {getLevelIcon(course.level)} {course.level.charAt(0).toUpperCase() + course.level.slice(1)}
                </span>
                <span className="price">R$ {course.price.toFixed(2)}</span>
              </div>
              <div className="rating">
                {'‚≠ê'.repeat(Math.floor(course.rating))}
                <span className="rating-number">({course.rating})</span>
              </div>
            </div>
          ))
        ) : (
          <div className="no-courses">
            <i className="fas fa-search"></i>
            <h3>Nenhum curso encontrado</h3>
            <p>Tente ajustar seus filtros ou fazer uma nova busca</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Courses;
